mapper.select:
SELECT count(*) from teacher_course c where status = 1
AND c.teacher_id = #{teacherId,jdbcType=INTEGER}
AND c.dir_id = #{dirId,jdbcType=INTEGER}
AND EXISTS (SELECT 'x' FROM teacher_directory td WHERE c.`dir_id` = td.`id` AND td.`permission` = #{permission} )

SELECT tc.`teacher_id`
FROM `teacher_course` tc
WHERE tc.`course_id` = #{courseId,jdbcType=INTEGER}
	AND tc.`status` = 1
ORDER BY tc.`id` ASC
LIMIT 1;

SELECT

FROM teacher_course
WHERE status = 1

	AND teacher_id = #{teacherId,jdbcType=INTEGER} 


	AND dir_id = #{dirId,jdbcType=INTEGER} 

ORDER BY order_id ASC,id ASC

SELECT

FROM teacher_course
WHERE status = 1 AND teacher_id = #{teacherId,jdbcType=INTEGER} 
ORDER BY create_time DESC
LIMIT #{limitNum}

SELECT id, order_id
FROM teacher_course
WHERE dir_id = #{dirId,jdbcType=INTEGER}
AND order_id >= #{startOrder,jdbcType=INTEGER}
AND order_id <= #{endOrder,jdbcType=INTEGER}
